
import dotenv from 'dotenv';
import fetch from 'node-fetch';
import path from 'path';

// Load .env from project root
dotenv.config({ path: path.resolve(process.cwd(), '.env') });

const GEMINI_API_KEY = process.env.VITE_GEMINI_API_KEY;

console.log("üöÄ Testing Gemini Vision API...");
console.log(`API Key found: ${GEMINI_API_KEY ? "Yes" : "No"}`);

if (!GEMINI_API_KEY) {
    console.error("‚ùå No Gemini API Key found in .env");
    process.exit(1);
}

async function testGemini() {
    console.log("Testing model: gemini-2.0-flash...");

    // 1x1 white pixel JPEG
    const base64Image = "";

    try {
        const response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=${GEMINI_API_KEY}`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                contents: [{
                    parts: [
                        { text: "What is this image?" },
                        { inline_data: { mime_type: "image/jpeg", data: base64Image } }
                    ]
                }]
            })
        });

        if (!response.ok) {
            const text = await response.text();
            throw new Error(`HTTP ${response.status}: ${text}`);
        }

        const data = await response.json();
        console.log("‚úÖ Success! Response:", JSON.stringify(data?.candidates?.[0]?.content?.parts?.[0]?.text, null, 2));

    } catch (e) {
        console.error("‚ùå Failed:", e.message);
    }
}

testGemini();
